import React, { useState, useEffect } from "react";
import InfiniteCarousel from "react-leaf-carousel";
import { Link } from "react-router-dom";
import {
    fetchTV,
} from "../../server";
// carousel data object
export default function Person({ match }) {
    let params = match.params;
    const [KnonwnPerson, setKnonwnPerson] = useState([]);
    useEffect(() => {
        const fetchAPI = async () => {
            setKnonwnPerson(await fetchTV(params.id));
        };
        fetchAPI();
    }, [params.id]);
    const KwonList1 = KnonwnPerson.slice(0, 12).map((c, i) => {
        return (
            <div className="col-md-3 col-sm-6" key={i}>
                <div className="card">
                    <Link to={`/movie/${c.id}`}>
                        <img className="img-fluid" src={c.poster} alt={c.title}></img>
                    </Link>
                </div>
                <div className="mt-3">
                    <p style={{ fontWeight: "bolder" }}>{c.title}</p>
                </div>
            </div >
        );
    });
    return (
        <InfiniteCarousel
            breakpoints={[
                {
                    breakpoint: 200,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                },
                {
                    breakpoint: 640,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 768,
                    settings: {
                        slidesToShow: 3,
                        slidesToScroll: 3
                    }
                }
            ]}
            lazyLoad={false}
            dots={true}
            showSides={true}
            sidesOpacity={0.5}
            sideSize={0.1}
            slidesToScroll={4}
            slidesToShow={4}
            scrollOnDevice={true}
        >
            {KwonList1}
        </InfiniteCarousel>
    );

};
