import React, { useState, useEffect } from "react";
import {
    fetchDiscover,
} from "../../service";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Link } from "react-router-dom";
import "../TV/Style.css";


export function Discover({ match }) {

    const [discover, setDiscover] = useState([]);

    useEffect(() => {
        const fetchAPI = async () => {
            setDiscover(await fetchDiscover(28));
        };
        fetchAPI();
    }, []);

    const displayTv = discover.slice(0, 9).map((i, index) => {
        return (

            <div className="carousel__face" key={index}>
                <Link to={`/detail/${i.id}`}>
                    <img className="poster" src={i.poster} alt={i.title}></img>
                </Link>
                <span>{i.title}</span>
            </div>



        );
    });
    const discoverList = discover.slice(0, 20).map((value, i) => {
        return (
            <div className="col-md-3 col-sm-6" key={i}>
                <div className="card">
                    <Link to={`/discover/tv/${value.id}`}>
                        <img className="img-fluid" src={value.poster} alt={value.title}></img>
                    </Link>
                </div>
                <div className="mt-3">
                    <p style={{ fontWeight: "bolder" }}>{value.title}</p>
                    {/* <p>Rated: {item.rating}</p> */}
                    {/* <ReactStars
        count={item.rating}
        size={20}
        color1={"#f4c10f"}
      ></ReactStars> */}
                </div>

            </div>
        );
    });


    return (

        <div className="main-container">
            {/* Hearder */}
            <div className="container">
                <div className="row">
                    <div className="col-md-8">
                        <nav>
                            <ul className="menu">
                                <li className="nav-hover"><a href="/">Home</a></li>
                                <li className="nav-hover"><a href="/trending/all/day">Trending</a></li>
                                <li className="nav-hover"><a href="/tv">TV Shows</a></li>
                                <li className="nav-hover"><a href="/people/popular">People</a></li>
                            </ul>
                        </nav>
                    </div>
                    <div className="col-md-4">
                        <form action="" className="search-bar">
                            <input type="search" name="search" pattern=".*\S.*" required />
                            <button type="submit" className="search-btn">
                                <span>Search</span>
                            </button>
                        </form>
                    </div>

                </div>
            </div>



            <div className="play-carousel">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="carousels">
                                {displayTv}
                            </div>
                        </div>
                    </div>
                </div>
            </div>


            <div className="tv">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <h2>Khám phá Phim</h2>
                            <div className="list-movie">
                                <div className="row">
                                    {discoverList}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="footer-main">

            </div>
           


        </div>
    );
}
