import React from "react";
import InfiniteCarousel from "react-leaf-carousel";

class RLCarousel extends React.Component(ImgDataIterator) {
    render() {
        return (
            <InfiniteCarousel
                breakpoints={[
                    {
                        breakpoint: 200,
                        settings: {
                            slidesToShow: 1,
                            slidesToScroll: 1
                        }
                    },
                    {
                        breakpoint: 640,
                        settings: {
                            slidesToShow: 2,
                            slidesToScroll: 2
                        }
                    },
                    {
                        breakpoint: 768,
                        settings: {
                            slidesToShow: 3,
                            slidesToScroll: 3
                        }
                    }
                ]}
                lazyLoad={false}
                dots={true}
                showSides={true}
                sidesOpacity={0.5}
                sideSize={0.1}
                slidesToScroll={4}
                slidesToShow={4}
                scrollOnDevice={true}
            >
                {ImgDataIterator}
            </InfiniteCarousel>
        );
    }

}
