import React, { useState, useEffect } from "react";
import {
  fetchMovies,
  fetchGenre,
  fetchMovieByGenre,
  fetchPersons,
  fetchTopratedMovie,
} from "../../service";
import RBCarousel from "react-bootstrap-carousel";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import ReactStars from "react-rating-stars-component";
import { Link } from "react-router-dom";
import "./Aminition/Home.css";
export function Home() {
  const [nowPlaying, setNowPlaying] = useState([]);
  const [genres, setGenres] = useState([]);
  const [movieByGenre, setMovieByGenre] = useState([]);
  const [persons, setPersons] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [video] = useState([]);

  useEffect(() => {
    const fetchAPI = async () => {
      setNowPlaying(await fetchMovies());
      setGenres(await fetchGenre());
      setMovieByGenre(await fetchMovieByGenre(28));
      setPersons(await fetchPersons());
      setTopRated(await fetchTopratedMovie());
    };
    fetchAPI();
  }, []);

  const handleGenreClick = async (genre_id) => {
    setMovieByGenre(await fetchMovieByGenre(genre_id));
  };


  const movies = nowPlaying.slice(0, 20).map((item, index) => {
    const youtubeUrl = "https://www.youtube.com/watch?v=";
    return (
      <div style={{ height: 500, width: "100%" }} key={index}>
        <div className="carousel-center">
          <img style={{ height: 600 }} src={item.backPoster} alt={item.title} />
        </div>
        <div className="carousel-center">
          <a href={youtubeUrl + video.key}>
            <i
              className="far fa-play-circle"
              style={{ fontSize: 95, color: "#f4c10f" }}
            ></i>
          </a>

        </div>
        <div
          className="carousel-caption"
          style={{ textAlign: "center", fontSize: 35 }}
        >
          <h1> {item.title} </h1>
        </div>
      </div>
    );
  });

  const genreList = genres.map((item, index) => {
    return (
      <nav className="link-effect-1">
        <div className="Link-hover" key={index} onClick={() => {
          handleGenreClick(item.id);
        }}><span data-hover={item.name}> {item.name}</span></div>
      </nav>
    );
  });

  const movieList = movieByGenre.slice(0, 12).map((item, index) => {
    return (
      <div className="col-md-2 col-sm-6" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          {/* <p>Rated: {item.rating}</p> */}
          {/* <ReactStars
            count={item.rating}
            size={20}
            color1={"#f4c10f"}
          ></ReactStars> */}
        </div>

      </div>
    );
  });

  const trendingPersons = persons.slice(0, 6).map((p, i) => {
    return (
      <div className="col-md-2 text-center" key={i}>
        <Link to={`/person/${p.id}`}>
          <img
            className="img-fluid rounded-circle mx-auto d-block"
            src={p.profileImg}
            alt={p.name}

          />
        </Link>

        <p className="font-weight-bold text-center">{p.name}</p>
        <p
          className="font-weight-light text-center"
          style={{ color: "#5a606b" }}
        >
          Trending for {p.known}
        </p>
      </div>
    );
  });
  // Các phim được đánh giá cao nhất 
  const topRatedList = topRated.slice(0, 4).map((item, index) => {
    return (
      <div className="col-md-3" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          <p>Rated: {item.rating}</p>
          <ReactStars
            count={item.rating}
            size={20}
            color1={"#f4c10f"}
          ></ReactStars>
        </div>
      </div>
    );
  });
  // Lấy các tấm hình:
  const images = movieByGenre.slice(0, 12).map((i) => {
    return (
      <img src={i.poster} alt={i.title} className="pic" />
    );
  });

  return (

    <div className="main-container">
      <div className="contain">
        <nav>
          <ul className="menu">
            <li className="nav-hover"><a href="/">Home</a></li>
            <li className="nav-hover"><a href="/trending/all/day">Trending</a></li>
            <li className="nav-hover"><a href="/discover/tv">TV</a></li>
            <li className="nav-hover"><a href="/people/popular">People</a></li>

          </ul>
        </nav>
        <div className="text-effect">
          <h1 className="neon" data-text="THE MOVIE">THE MOVIE</h1>
          <div className="gradient"></div>
          <div className="spotlight"></div>
        </div>
      </div>

      <div className="baner-movie">
        <div className="container">
          <div className="row">
            <div className="col">
              <RBCarousel
                autoplay={true}
                pauseOnVisibility={true}
                slidesshowSpeed={5000}
                version={4}
                indicators={false}
              >
                {movies}
              </RBCarousel>
            </div>
          </div>
        </div>
      </div>
      {/* Thể loại phim */}
      <div className="genre-list">
        <div className="container">
          <div className="row">
            <div className="col">
              <div id="content">
                <section className="links">
                  {genreList}
                </section>
              </div>
            </div>
          </div>
        </div>
      </div>


      {/* Danh sách phim */}
      <div className="list-movie">
        <div className="container">
          <div className="row">
            <div className="col-md-2">
              {/* Thiết kế đang suy nghĩ */}
            </div>
            <div className="col-md-10">
              <div className="list-movie">
                <div className="row mt-3">{movieList}</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Xu hướng phim */}
      <div className="trending-list">
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <p className="font-weight-bold-1  line-1" style={{ color: "#5a606b" }}>
                <a href="#" className="hover-trending">TRENDING PERSONS</a>  ON THIS WEEK
              </p>
              <div className="list-movie-trending">
                <div className="row">
                  {trendingPersons}
                </div>

              </div>
            </div>
          </div>
        </div>
      </div>


      {/* Phim có đánh giá cao nhất */}
      <div className="rating-movie">
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <p className="font-weight-bold-1  line-1" style={{ color: "#5a606b" }}>
                <a href="#" className="hover-trending"> TOP RATED MOVIES</a>
              </p>
              <div className="top-rating-list">
                <div className="row">
                  {topRatedList}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>


      <hr className="mt-5" style={{ border: "1px solid #5a606b" }}></hr>
      <div className="footer-container">
        <div className="container">
          <div className="row">
            <div className="col-md-5 col-sm-6" style={{ color: "#5a606b" }}>
              <h3>THÔNG TIN</h3>
              <div className="email-title">
                Email :
                <a href="#">ngoctam2303001@gmail.com</a>
              </div>
              <p>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus,
                perspiciatis? Numquam, enim illo voluptatum neque facere aut sed ut
                dolore nihil? Nulla sit, recusandae ea tenetur rerum deserunt sequi
                earum?
              </p>
              <div className="button">
                <div className="icon">
                  <i className="fab fa-facebook"></i>
                </div>
                <span>Facebook</span>
              </div>
              <div className="button">
                <div className="icon">
                  <i className="fab fa-instagram"></i>
                </div>
                <span>Instagram</span>
              </div>
              <div className="button">
                <div className="icon">

                  <i className="fab fa-twitter"></i>
                </div>
                <span>Twitter</span>
              </div>

              <div className="button">
                <div className="icon">
                  <i className="fab fa-youtube"></i>
                </div>
                <span>Youtube</span>
              </div>
            </div>
            <div className="col-md-7 col-sm-6" style={{ color: "#5a606b" }}>
              <div className="main-top">
                <div className="container">
                  <div className="row">
                    <div className="col-md-12">
                      <div className="bg-blog">
                        <div className="pic-ctn bore">
                          {images}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>



    </div>
  );
}
