import React, { useState, useEffect } from "react";
import {
    // fetchMovieDetail,
    fetchPeople,
} from "../../service";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Link } from "react-router-dom";
import "./People.css";


export function People({ match }) {
    // let params = match.params;
    // const [setDetail] = useState([]);
    const [people, setPeople] = useState([]);
    useEffect(() => {
        const fetchAPI = async () => {
            // setDetail(await fetchMovieDetail(params.id));

            setPeople(await fetchPeople(28));
        };
        fetchAPI();
    }, []);

    const peopleList = people.slice(0, 20).map((value, i) => {
        return (
            <div className="col-md-3 col-sm-6" key={i}>
                <div className="cards-img">
                    <Link to={`/person/${value.id}`}>
                        <img className="images" src={value.img} alt={value.title}></img>
                    </Link>
                    <div className="overlay">
                        <a className="text" href={`/person/${value.id}`}>{value.title}</a>
                    </div>
                </div>
                <div className="d">
                    {value.name}
                </div>
            </div>
        );
    });
 // Lấy các tấm hình:
 const images = movieByGenre.slice(0, 12).map((i) => {
    return (
      <img src={i.poster} alt={i.title} className="pic" />
    );
  });





    return (

        <div className="container">

            <div className="row mr-10 mt-2">

                <nav>
                    <ul className="menu">
                        <li className="nav-hover"><a href="/">Home</a></li>
                        <li className="nav-hover"><a href="/trending/all/day">Trending</a></li>
                        <li className="nav-hover"><a href="/discover/tv">TV Shows</a></li>
                        <li className="nav-hover"><a href="/people/popular">People</a></li>
                    </ul>
                </nav>

            </div>

            <div className="row mt-3">

                <div className="col-md-12">
                    <div className="list-movie">
                        <div className="row mt-3">{peopleList}</div>
                    </div>
                </div>
            </div>

            <hr className="mt-5" style={{ border: "1px solid #5a606b" }}></hr>
      <div className="footer">
        <div className="container">
          <div className="row">
            <div className="col-md-5 col-sm-6" style={{ color: "#5a606b" }}>
              <h3>THÔNG TIN</h3>
              <div className="email-title">
                Email :
                <a href="#">ngoctam2303001@gmail.com</a>
              </div>
              <p>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus,
                perspiciatis? Numquam, enim illo voluptatum neque facere aut sed ut
                dolore nihil? Nulla sit, recusandae ea tenetur rerum deserunt sequi
                earum?
              </p>
              <div className="button">
                <div className="icon">
                  <i className="fab fa-facebook"></i>
                </div>
                <span>Facebook</span>
              </div>
              <div className="button">
                <div className="icon">

                  <i className="fab fa-instagram"></i>

                </div>
                <span>Instagram</span>
              </div>
              <div className="button">
                <div className="icon">

                  <i className="fab fa-twitter"></i>
                </div>
                <span>Twitter</span>
              </div>

              <div className="button">
                <div className="icon">
                  <i className="fab fa-youtube"></i>
                </div>
                <span>Youtube</span>
              </div>
            </div>
            <div className="col-md-7 col-sm-6" style={{ color: "#5a606b" }}>
              <div className="main-top">
                <div className="container">
                  <div className="row">
                    <div className="col-md-12">
                      <div className="bg-blog">
                        <div className="pic-ctn bore">
                          {images}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>



        </div>
    );
}
