import React, { useState, useEffect } from "react";
import {
    fetchTVDetail,
    fetchTVVideos,
    fetchTVCredits,
} from "../../service";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Modal } from "react-bootstrap";
import ReactPlayer from "react-player";
import ReactStars from "react-rating-stars-component";
import "../home/Aminition/Home.css";
export function DiscoverDetail({ match }) {
    let params = match.params;
    let genres = [];
    const [isOpen, setIsOpen] = useState(false);
    const [detail, setDetail] = useState([]);
    const [video, setVideo] = useState([]);
    const [credits, setCredits] = useState([]);


    useEffect(() => {
        const fetchAPI = async () => {
            setDetail(await fetchTVDetail(params.id));
            setVideo(await fetchTVVideos(params.id));
            setCredits(await fetchTVCredits(params.id));
        };
        fetchAPI();
    }, [params.id]);

    genres = detail.genres;
    const MoviePalyerModal = (props) => {
        const youtubeUrl = "https://www.youtube.com/watch?v=";
        return (
            <Modal
                {...props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title
                        id="contained-modal-title-vcenter"
                        style={{ color: "#000000", fontWeight: "bolder" }}
                    >
                        Review {detail.name}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ backgroundColor: "#000000" }}>
                    <ReactPlayer
                        className="container-fluid"
                        url={youtubeUrl + video.key}
                        playing
                        width="100%"
                    ></ReactPlayer>
                </Modal.Body>
            </Modal>
        );
    };

    let genresList;
    if (genres) {
        genresList = genres.map((g, i) => {
            return (
                <li className="list-inline-item" key={i}>
                    <button type="button" className="btn btn-outline-info">
                        {g.name}
                    </button>
                </li>
            );
        });
    }

    const listCredits = credits.slice(0, 4).map((c, i) => {
        return (
            <div className="col-md-3 text-center" key={i}>
                <Link to={`/person/${c.id}`}>
                    <img
                        className="img-fluid rounded-circle mx-auto d-block"
                        src={c.img}
                        alt={c.name}

                    />
                </Link>
                <p className="font-weight-bold text-center">{c.name}</p>
                <p
                    className="font-weight-light text-center"
                    style={{ color: "#5a606b" }}
                >
                    {c.character}
                </p>
            </div>
        );
    });



    return (
        <div className="main-container">
            <div className="container">
                <div className="row">
                    <nav>
                        <ul className="menu">
                            <li className="nav-hover"><a href="/">Home</a></li>
                            <li className="nav-hover"><a href="/trending/all/day">Trending</a></li>
                            <li className="nav-hover"><a href="/discover/tv">TV Shows</a></li>
                            <li className="nav-hover"><a href="/people/popular">People</a></li>
                        </ul>
                    </nav>
                </div>
            </div>
            {/* Chi tiết phim */}
            <div className="movie-trailer">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <MoviePalyerModal
                                show={isOpen}
                                onHide={() => {
                                    setIsOpen(false);
                                }}
                            ></MoviePalyerModal>
                            <div className="col text-center" style={{ width: "100%" }}>
                                <img
                                    className="img-fluid"
                                    src={`http://image.tmdb.org/t/p/original/${detail.backdrop_path}`}
                                    alt={detail.name}
                                ></img>
                                <div className="carousel-center">
                                    <i
                                        onClick={() => setIsOpen(true)}
                                        className="far fa-play-circle"
                                        style={{ fontSize: 95, color: "#f4c10f", cursor: "pointer" }}
                                    ></i>
                                </div>
                                <div
                                    className="carousel-caption"
                                    style={{ textAlign: "center", fontSize: 40 }}
                                >
                                    {detail.name}
                                </div>
                            </div>


                        </div>
                    </div>
                </div>
            </div>





            <div className="detail-movie">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <p style={{ color: "#5a606b", fontWeight: "bolder" }}>GENRE</p>
                            <ul className="list-inline">{genresList}</ul>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-12">
                            <div className="text-center">
                                <ReactStars
                                    count={detail.vote_average}
                                    size={20}
                                    color1={"#f4c10f"}
                                ></ReactStars>
                            </div>
                            <div className="mt-3">
                                <p style={{ color: "#5a606b", fontWeight: "bolder" }}>OVERVIEW</p>
                                {detail.overview}
                            </div>
                        </div>
                    </div>
                    <div className="detail-list-movie">
                        <div className="row">
                            <div className="col-md-3">
                                <p style={{ color: "#5a606b", fontWeight: "bolder" }}>RELEASE DATE</p>
                                <p style={{ color: "#f4c10f" }}>{detail.release_date}</p>
                            </div>
                            <div className="col-md-3">
                                <p style={{ color: "#5a606b", fontWeight: "bolder" }}>RUN TIME</p>
                                <p style={{ color: "#f4c10f" }}>{detail.runtime}</p>
                            </div>
                            <div className="col-md-3">
                                <p style={{ color: "#5a606b", fontWeight: "bolder" }}>BUDGET</p>
                                <p style={{ color: "#f4c10f" }}>{detail.budget}</p>
                            </div>
                            <div className="col-md-3">
                                <p style={{ color: "#5a606b", fontWeight: "bolder" }}>HOMEPAGE</p>
                                <p style={{ color: "#f4c10f" }}>{detail.homepage}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


            {/* Danh sách tác giả */}
            <div className="person-list">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <p style={{ color: "#5a606b", fontWeight: "bolder" }}>Diễn viên</p>
                            <div className="cast-person-list">
                                <div className="row">
                                    {lis}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>





            {/* Footer */}
            <div className="footer">
                <hr className="mt-5" style={{ borderTop: "1px solid #5a606b" }}></hr>
                <div className="container">
                    <div className="row">
                        <div className="col-md-8 col-sm-6">
                            <h3>ABOUT ME</h3>
                            <p>
                                Lorem ipsum dolor sit, amet consectetur adipisicing elit. Animi
                                error earum perspiciatis praesentium sint ipsum provident blanditiis
                                pariatur necessitatibus voluptas, cum, atque iste eligendi autem,
                                culpa cupiditate placeat facilis repellat.
                            </p>
                            <p>
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus,
                                perspiciatis? Numquam, enim illo voluptatum neque facere aut sed ut
                                dolore nihil? Nulla sit, recusandae ea tenetur rerum deserunt sequi
                                earum?
                            </p>
                            <div className="button">
                                <div className="icon">
                                    <i className="fab fa-facebook"></i>
                                </div>
                                <span>Facebook</span>
                            </div>
                            <div className="button">
                                <div className="icon">

                                    <i className="fab fa-instagram"></i>

                                </div>
                                <span>Instagram</span>
                            </div>
                            <div className="button">
                                <div className="icon">

                                    <i className="fab fa-twitter"></i>
                                </div>
                                <span>Twitter</span>
                            </div>

                            <div className="button">
                                <div className="icon">
                                    <i className="fab fa-youtube"></i>
                                </div>
                                <span>Youtube</span>
                            </div>
                        </div>
                        <div className="col-md-4 col-sm-6">
                            <h3>KEEP IN TOUCH</h3>
                            <ul className="list-unstyled">
                                <li>
                                    <p>
                                        <strong>
                                            <i className="fas fa-map-marker-alt"></i> Address:
                                        </strong>{" "}
                                        city, state, country
                                    </p>
                                </li>
                                <li>
                                    <p>
                                        <strong>
                                            <i className="fas fa-map-marker-alt"></i> Phone:
                                        </strong>{" "}
                                        +01 00 00 00
                                    </p>
                                </li>
                                <li>
                                    <p>
                                        <strong>
                                            <i className="fas fa-envelope"></i> Email:
                                        </strong>{" "}
                                        info@infomail.com
                                    </p>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    );
}
