import React, { useState, useEffect } from "react";
import {
  fetchMovies,
  fetchGenre,
  fetchMovieByGenre,
  fetchPersons,
  fetchTopratedMovie,
} from "../../service";
import RBCarousel from "react-bootstrap-carousel";
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Link } from "react-router-dom";
import ReactStars from "react-rating-stars-component";
import { Header } from "../Header/Header";
import "./Aminition/Home.css";


export function Home() {
  const [nowPlaying, setNowPlaying] = useState([]);
  const [genres, setGenres] = useState([]);
  const [movieByGenre, setMovieByGenre] = useState([]);
  const [persons, setPersons] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [video, setVideo] = useState([]);

  useEffect(() => {
    const fetchAPI = async () => {
      setNowPlaying(await fetchMovies());
      setGenres(await fetchGenre());
      setMovieByGenre(await fetchMovieByGenre(28));
      setPersons(await fetchPersons());
      setTopRated(await fetchTopratedMovie());
    };
    fetchAPI();
  }, []);

  const handleGenreClick = async (genre_id) => {
    setMovieByGenre(await fetchMovieByGenre(genre_id));
  };
  const handlePersonClick = async (person_id) => {
    setPersons(await fetchPersons(setPersons));
  };

  const movies = nowPlaying.slice(0, 10).map((item, index) => {
    const youtubeUrl = "https://www.youtube.com/watch?v=";
    return (

      <div style={{ height: 500, width: "100%" }} key={index}>
        <div className="carousel-center">
          <img style={{ height: 600 }} src={item.backPoster} alt={item.title} />
        </div>
        <div className="carousel-center">
          <a href={youtubeUrl + video.key}>
            <i
              className="far fa-play-circle"
              style={{ fontSize: 95, color: "#f4c10f" }}
            ></i>
          </a>

        </div>
        <div
          className="carousel-caption"
          style={{ textAlign: "center", fontSize: 35 }}
        >
          {item.title}
        </div>
      </div>
    );
  });

  const genreList = genres.map((item, index) => {
    return (
      <li className="list-inline-item" key={index}>
        <button
          type="button"
          className="btn btn-outline-info"
          onClick={() => {
            handleGenreClick(item.id);
          }}
        >
          {item.name}
        </button>
      </li>
    );
  });

  const movieList = movieByGenre.slice(0, 4).map((item, index) => {
    return (
      <div className="col-md-3 col-sm-6" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          <p>Rated: {item.rating}</p>
          <ReactStars
            count={item.rating}
            size={20}
            color1={"#f4c10f"}
          ></ReactStars>
        </div>
      </div>
    );
  });

  const trendingPersons = persons.slice(0, 4).map((p, i) => {
    return (
      <div className="col-md-3 text-center" key={i}>
        <img
          className="img-fluid rounded-circle mx-auto d-block"
          src={p.profileImg}
          alt={p.name}
          onClick={() => {
            handlePersonClick(p.person_id);
          }}
        ></img>
        <p className="font-weight-bold text-center">{p.name}</p>
        <p
          className="font-weight-light text-center"
          style={{ color: "#5a606b" }}
        >
          Trending for {p.known}
        </p>
      </div>
    );
  });

  const topRatedList = topRated.slice(0, 4).map((item, index) => {
    return (
      <div className="col-md-3" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          <p>Rated: {item.rating}</p>
          <ReactStars
            count={item.rating}
            size={20}
            color1={"#f4c10f"}
          ></ReactStars>
        </div>
      </div>
    );
  });

  return (
    <div className="container">
      <div className="row mr-10">
        <nav class="navbar navbar-expand-lg navbar-dark">
          <div class="container-fluid">
            <button
              class="navbar-toggler"
              type="button"
              data-mdb-toggle="collapse"
              data-mdb-target="#navbarExample01"
              aria-controls="navbarExample01"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <i class="fas fa-bars"></i>
            </button>
            <div class="collapse navbar-collapse" id="navbarExample01">
              <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                <li class="nav-item active">
                  <a class="nav-link" aria-current="page" href="#">Home</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">Features</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">Pricing</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">About</a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
      <div className="row mt-2">
        <div className="col">
          <RBCarousel
            autoplay={true}
            pauseOnVisibility={true}
            slidesshowSpeed={5000}
            version={4}
            indicators={false}
          >
            {movies}
          </RBCarousel>
        </div>
      </div>
      <div className="row mt-3">
        <div className="col">
          <ul className="list-inline">{genreList}</ul>
        </div>
      </div>

      <div className="row mt-3">
        <div className="col">
          <div className="float-right">
            <i className="far fa-arrow-alt-circle-right"></i>
          </div>
        </div>
      </div>
      <div className="row mt-3">{movieList}</div>

      <div className="row mt-3">
        <div className="col">
          <p className="font-weight-bold" style={{ color: "#5a606b" }}>
            TRENDING PERSONS ON THIS WEEK
          </p>
        </div>
      </div>

      <div className="row mt-3">
        <div className="col">
          <div className="float-right">
            <i className="far fa-arrow-alt-circle-right"></i>
          </div>
        </div>
      </div>
      <div className="row mt-3">{trendingPersons}</div>

      <div className="row mt-3">
        <div className="col">
          <p className="font-weight-bold" style={{ color: "#5a606b" }}>
            TOP RATED MOVIES
          </p>
        </div>
      </div>

      <div className="row mt-3">
        <div className="col">
          <div className="float-right">
            <i className="far fa-arrow-alt-circle-right"></i>
          </div>
        </div>
      </div>
      <div className="row mt-3">{topRatedList}</div>

      <hr className="mt-5" style={{ borderTop: "1px solid #5a606b" }}></hr>
      <div className="container">
        <div className="row mt-3 mb-5">
          <div className="col-md-8 col-sm-6" style={{ color: "#5a606b" }}>
            <h3>ABOUT ME</h3>
            <p>
              Lorem ipsum dolor sit, amet consectetur adipisicing elit. Animi
              error earum perspiciatis praesentium sint ipsum provident blanditiis
              pariatur necessitatibus voluptas, cum, atque iste eligendi autem,
              culpa cupiditate placeat facilis repellat.
            </p>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus,
              perspiciatis? Numquam, enim illo voluptatum neque facere aut sed ut
              dolore nihil? Nulla sit, recusandae ea tenetur rerum deserunt sequi
              earum?
            </p>
            <div className="button">
              <div className="icon">
                <i className="fab fa-facebook"></i>
              </div>
              <span>Facebook</span>
            </div>
            <div className="button">
              <div className="icon">

                <i className="fab fa-instagram"></i>

              </div>
              <span>Instagram</span>
            </div>
            <div className="button">
              <div className="icon">

                <i className="fab fa-twitter"></i>
              </div>
              <span>Twitter</span>
            </div>

            <div className="button">
              <div className="icon">
                <i className="fab fa-youtube"></i>
              </div>
              <span>Youtube</span>
            </div>
          </div>


          <div className="col-md-4 col-sm-6" style={{ color: "#5a606b" }}>
            <h3>KEEP IN TOUCH</h3>
            <ul className="list-unstyled">
              <li>
                <p>
                  <strong>
                    <i className="fas fa-map-marker-alt"></i> Address:
                  </strong>{" "}
                  HoChiMinh city
                </p>
              </li>
              <li>
                <p>
                  <strong>
                    <i className="fas fa-map-marker-alt"></i> Phone:
                  </strong>{" "}
                  +84939461842
                </p>
              </li>
              <li>
                <p>
                  <strong>
                    <i className="fas fa-envelope"></i> Email:
                  </strong>{" "}
                  ngoctam2303001@gmail.com
                </p>
              </li>
            </ul>
          </div>
        </div>

      </div>


    </div>
  );
}
